const a="http://server-nodejs.cit.byui.edu:3000/";function o(s){if(s.ok)return s.json();throw new Error("Bad Response")}class c{constructor(){}async getCategories(){try{const r=await fetch(`${a}products/categories`);return(await o(r)).Result}catch(r){return console.error("Error fetching categories:",r),[]}}async getAllProducts(){try{const r=await fetch(`${a}products/all`);return(await o(r)).Result.map(t=>({id:t.id,name:t.name,category:t.category,price:t.price,stock:t.stock,brand:t.brand,rating:t.rating,created_at:t.created_at,images:t.images||[]}))}catch(r){return console.error("Error fetching all products:",r),[]}}async getData(r){try{const e=await fetch(`${a}products/search/${r}`);return(await o(e)).Result}catch(e){return console.error("Error fetching products:",e),[]}}async findProductById(r){try{const e=await fetch(`${a}product/${r}`);return(await o(e)).Result}catch(e){return console.error("Error fetching product by ID:",e),null}}}export{c as P};
